import React from 'react';

import Card from './Card';
import Button from './Button';
import styles from './ErrorModal.module.css';

const ErrorModal = props =>
{
    return(
        <Card currentStyle={styles.modal}>
            <header currentStyle={styles.header}>
                <h2>{props.title}</h2>
            </header>
            <div currentStyle={styles.content}>
                <p>{props.message}</p>
            </div>
            <footer currentStyle={styles.actions}>
                <Button>Close</Button> 
            </footer>
        </Card>
    );
};
export default ErrorModal;
