import React, { useState } from 'react';
import Card from '../UI/Card';
import styles from './AddUser.module.css';
import Button from '../UI/Button';

const AddUser = props => {

    const [enteredUsername, setEnteredUsername] = useState(''); // array destructuring

    const usernameChangeHandler = (event) =>
    {

        setEnteredUsername(event.target.value);
        console.log("enteredUsername = " + enteredUsername);
    }


    /* function to handle when a user submits the form below */
    const addUserHandler = (event) => {
        event.preventDefault(); // stop default HTTP request
    };

    return ( 
        <Card inputStyling={styles.input}>
            <form onSubmit={addUserHandler}>
                <label htmlFor="username" onChange={usernameChangeHandler}>Username</label>
                <input id="username" type="text"/>
                <label htmlFor="age">Age (Years)</label>
                <input id="age" type="number"/>
                <Button type="submit">Add User</Button>
            </form>
        </Card>
    );




};

export default AddUser;
